mapper.select:
    	SELECT * 
    	  FROM TB_PUSH_TRANSMIT_REQ
    	 WHERE 1=1    	 
    	   AND APP_ID = #{appId}

		   AND REQ_DT  >  #{fromDate}


		   AND REQ_DT  <  #{toDate}

		LIMIT #{page.offset}, #{page.size}

mapper.update:
UPDATE TB_PUSH_TRANSMIT_REQ
   SET ERR_MSG = #{errMsg}
 WHERE TRANSMIT_REQ_ID = #{transmitReqId}

UPDATE TB_PUSH_TRANSMIT
   SET RES_STATUS_CD = #{resStatusCd}, 
       RES_DATA = #{resData}, 
       END_DT = #{endDt}
 WHERE TRANSMIT_REQ_ID = #{transmitReqId}
   AND TARGET = #{target}
